<style lang="sass">
/* 全体の入れ物 */
.main-visual
  height: 90vh
  position: relative

/* 画像の入れ物 */
.svg-container
  position: absolute
  width: 72%
  height: 32%
  margin: 8% 0 0 12%

/* 画像 */
.NuxtLogo
  width: 100%
  fill: #eee
  &:nth-child(2)
    margin-top: -6%
  +pc-md-view
    width: 112%
  +sp-view
    transform: rotate(90deg)
    width: 200%
    margin: 80% 0 0 -56%

/* 下からアニメーション */
.logo-anime1
  overflow: hidden
.wrapped
  animation: anime 1.5s cubic-bezier(0.6, 0.6, 0.22, 1)


/* aboutページのとき */
.content-about
  .NuxtLogo
    opacity: .3

/* indexページのとき */
.content-index
  +sp-view
    .NuxtLogo
      pointer-events: none

@keyframes anime
  0%
    transform: translateY(32vw)
  100%
    transform: translateY(0)
</style>
