<script>

export default{
  name: 'animation',
 mounted() {
   // rootからbottom:-150pxの位置で発火
   this.onIntersect()
 },
 methods: {
   onIntersect() {
     const options = {
       root: null,
       rootMargin: "-72px",
       threshold: 0.6
     }
     const observer = new IntersectionObserver(this.addShowClass)
     observer.observe(this.$el)
   },
   addShowClass(entries) {
     for(const e of entries) {
       if (e.intersectionRatio > 0) {
         e.target.classList.add("showd")
         e.target.classList.remove("sns-all")
        }
     }
   }
 }
}
</script>
<style lang="sass">

/* 全体 */
.sns-container
  display: flex
  justify-content: flex-end
  width: 100%
  margin: 8% 12% 0 0
  &.showd
    .sns-item
      transform: translate(0, 0)

/* トップの場合 */
.content-index
  .sns-container
    margin-right: 16%
    width: 84%

